const db = require("../../../lib/db");
const escape = require("sql-template-strings");

// GET BLOG POST

module.exports = async (req, res) => {
    const {
        query: { uid }
    } = req
    // let rq = req.params;
    const post = await db.query(escape`SELECT * FROM blog_posts WHERE uid = "${uid}"`)
    console.log(post)
    res.status(200).json(post)
}
